import react, {useEffect} from 'react'
import './App.css'
import SearchIcon from './search.svg'

const API_URL = "http://www.omdbapi.com?apikey=9002e45f"
const movie1 = {
    "Title": "Shrek 2",
    "Year": "2004",
    "imdbID": "tt0298148",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BMDJhMGRjN2QtNDUxYy00NGM3LThjNGQtMmZiZTRhNjM4YzUxL2ltYWdlL2ltYWdlXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg"
}


const App = () => {

    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`)
        const data = await response.json();

        console.log(data.Search)
    }

    useEffect(() => {
        searchMovies("Shrek")
    }, [])

    return (
        <div className="app">
            <h1>MovieLand</h1>

            <div className='search'>
                <input placeholder="search for movies"
                value="Superman"
                onChange = {() => {

                }}
                />
                <img src={SearchIcon} alt={"Search"} onclick= {() => {

                }} ></img>

            </div>

            <div className="container">
                <div className="movie">
                    <div>
                        <p>{movie1.Year}</p>
                    </div>

                    <div>
                        <img src={movie1.Poster !== "N/a" ?  movie1.Poster : 'https://via.placeholder.com/400'} alat={movie1.Title}></img>
                    </div>
                    <div>
                        <span>{movie1.Type}</span>
                    </div>
                </div>

            </div>
        </div>
    
    
    );
}

export default App
